mapper.select:
select * from message


		and id=#{id}


		and session_id=#{sessionId}


		and sender_id=#{senderId}


		and sender_nick_name=#{senderNickName}


		and receiver_id=#{receiverId}


		and type=#{type}


		and content=#{content}


		and file_name=#{fileName}


		and file_type=#{fileType}


		and file_size=#{fileSize}


		and send_time=#{sendTime}


		and contact_type=#{contactType}


		and status=#{status}

;

select count(*) from message


		and id=#{id}


		and session_id=#{sessionId}


		and sender_id=#{senderId}


		and sender_nick_name=#{senderNickName}


		and receiver_id=#{receiverId}


		and type=#{type}


		and content=#{content}


		and file_name=#{fileName}


		and file_type=#{fileType}


		and file_size=#{fileSize}


		and send_time=#{sendTime}


		and contact_type=#{contactType}


		and status=#{status}

;

select * from message


		and id=#{message.id}


		and session_id=#{message.sessionId}


		and sender_id=#{message.senderId}


		and sender_nick_name=#{message.senderNickName}


		and receiver_id=#{message.receiverId}


		and type=#{message.type}


		and content=#{message.content}


		and file_name=#{message.fileName}


		and file_type=#{message.fileType}


		and file_size=#{message.fileSize}


		and send_time=#{message.sendTime}


		and contact_type=#{message.contactType}


		and status=#{message.status}


limit #{offset},#{count};
