mapper.select:
select * from session


		and user_id=#{userId}


		and contact_id=#{contactId}


		and contact_nickname=#{contactNickname}


		and id=#{id}


		and last_message=#{lastMessage}


		and last_receive_time=#{lastReceiveTime}

;

select count(*) from session


		and user_id=#{userId}


		and contact_id=#{contactId}


		and contact_nickname=#{contactNickname}


		and id=#{id}


		and last_message=#{lastMessage}


		and last_receive_time=#{lastReceiveTime}

;

select * from session


		and user_id=#{session.userId}


		and contact_id=#{session.contactId}


		and contact_nickname=#{session.contactNickname}


		and id=#{session.id}


		and last_message=#{session.lastMessage}


		and last_receive_time=#{session.lastReceiveTime}


limit #{offset},#{count};
