mapper.select:
select * from user


		and id=#{id}


		and email=#{email}


		and password=#{password}


		and status=#{status}


		and permission=#{permission}


		and nickname=#{nickname}


		and sex=#{sex}


		and personal_signature=#{personalSignature}


		and area=#{area}


		and create_time=#{createTime}


		and last_login_time=#{lastLoginTime}


		and last_logout_time=#{lastLogoutTime}

;

select count(*) from user


		and id=#{id}


		and email=#{email}


		and password=#{password}


		and status=#{status}


		and permission=#{permission}


		and nickname=#{nickname}


		and sex=#{sex}


		and personal_signature=#{personalSignature}


		and area=#{area}


		and create_time=#{createTime}


		and last_login_time=#{lastLoginTime}


		and last_logout_time=#{lastLogoutTime}

;

select * from user


		and id=#{user.id}


		and email=#{user.email}


		and password=#{user.password}


		and status=#{user.status}


		and permission=#{user.permission}


		and nickname=#{user.nickname}


		and sex=#{user.sex}


		and personal_signature=#{user.personalSignature}


		and area=#{user.area}


		and create_time=#{user.createTime}


		and last_login_time=#{user.lastLoginTime}


		and last_logout_time=#{user.lastLogoutTime}


limit #{offset},#{count};
